import {
  ComponentContext,
  ComponentFile,
  ComponentTemplate,
} from '@teambit/generator';
import { indexFile } from './files/index-file';
import { AppFile } from './files/app';
import { reactAppFile } from './files/react-app';
import { compositionFile } from './files/composition';
import { AppRootFile } from './files/app-root';

// TODO: replace this with "import {ComponentTemplateOptions} from '@teambit/generator';" once v1.3 is released
export type ReactAppTemplateOptions = Partial<Pick<ComponentTemplate, 'name' | 'description' | 'hidden' | 'env'>>;

export class ReactAppTemplate implements ComponentTemplate {
  constructor(
    readonly name = 'react-app',
    readonly description = 'a template for generating react app components',
    readonly hidden = false,
    readonly env?: string
  ) {}

  generateFiles(context: ComponentContext): ComponentFile[] {
    return [
      indexFile(context),
      compositionFile(context),
      AppFile(context),
      reactAppFile(context),
      AppRootFile(context),
    ];
  }

  static from(options: ReactAppTemplateOptions = {}) {
    return () =>
      new ReactAppTemplate(options.name, options.description, options.hidden, options.env);
  }
}
